import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ItineraryForm() {
  const [preferences, setPreferences] = useState({
    location: '',
    activityTypes: [],
    budget: '',
    travelCompanions: '',
    specialInterests: ''
  });
  
  const [itinerary, setItinerary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [backendStatus, setBackendStatus] = useState('checking'); // Add backend status state
  const [connectionStatus, setConnectionStatus] = useState('checking');
  useEffect(() => {
    const testConnection = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/test');
        
        if (response.data.message === "Backend is working!") {
          setConnectionStatus('connected');
        } else {
          setConnectionStatus('invalid-response');
        }
      } catch (error) {
        if (error.response) {
          // Server responded with error status
          setConnectionStatus(`server-error-${error.response.status}`);
        } else if (error.request) {
          // No response received
          setConnectionStatus('no-response');
        } else {
          // Other errors
          setConnectionStatus('request-failed');
        }
      }
    };
    
    testConnection();
  }, []);

  const activityOptions = ['beach', 'hiking', 'culture', 'food', 'shopping', 'wildlife'];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPreferences(prev => ({ ...prev, [name]: value }));
  };

  const handleActivityChange = (activity) => {
    setPreferences(prev => {
      const newActivities = prev.activityTypes.includes(activity)
        ? prev.activityTypes.filter(a => a !== activity)
        : [...prev.activityTypes, activity];
      
      return { ...prev, activityTypes: newActivities };
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.post(
        'http://localhost:5000/api/itinerary',
        preferences
      );
      setItinerary(response.data);
    } catch (error) {
      console.error('Error generating itinerary:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTopRecommendations = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        'http://localhost:5000/api/recommendations'
      );
      setItinerary({
        summary: "Top recommendations in Mauritius",
        recommendations: response.data.recommendations
      });
    } catch (error) {
      console.error('Error getting recommendations:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-blue-800 border-b pb-3">
        Plan Your Mauritius Adventure
      </h2>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Location */}
        <div>
          <label className="block mb-2 font-medium text-gray-700">
            Preferred Location:
          </label>
          <select 
            name="location" 
            value={preferences.location}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Anywhere in Mauritius</option>
            <option value="North">North (Grand Baie, Trou aux Biches)</option>
            <option value="South">South (Le Morne, Chamarel)</option>
            <option value="East">East (Ile aux Cerfs, Belle Mare)</option>
            <option value="West">West (Flic en Flac, Tamarin)</option>
          </select>
        </div>
        
        {/* Activity Types */}
        <div>
          <label className="block mb-2 font-medium text-gray-700">
            Preferred Activities:
          </label>
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-3">
            {activityOptions.map(activity => (
              <div key={activity} className="flex items-center">
                <input
                  type="checkbox"
                  id={activity}
                  checked={preferences.activityTypes.includes(activity)}
                  onChange={() => handleActivityChange(activity)}
                  className="h-5 w-5 text-blue-600 rounded focus:ring-blue-500"
                />
                <label htmlFor={activity} className="ml-2 text-gray-700 capitalize">
                  {activity}
                </label>
              </div>
            ))}
          </div>
        </div>
        
        {/* Budget */}
        <div>
          <label className="block mb-2 font-medium text-gray-700">
            Budget Range:
          </label>
          <select 
            name="budget" 
            value={preferences.budget}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Any budget</option>
            <option value="low">Budget-friendly ($)</option>
            <option value="medium">Moderate ($$)</option>
            <option value="high">Luxury ($$$)</option>
          </select>
        </div>
        
        {/* Travel Companions */}
        <div>
          <label className="block mb-2 font-medium text-gray-700">
            Traveling With:
          </label>
          <select 
            name="travelCompanions" 
            value={preferences.travelCompanions}
            onChange={handleChange}
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Not specified</option>
            <option value="solo">Solo</option>
            <option value="couple">Couple</option>
            <option value="family">Family</option>
            <option value="friends">Friends</option>
          </select>
        </div>
        
        {/* Special Interests */}
        <div>
          <label className="block mb-2 font-medium text-gray-700">
            Special Interests (optional):
          </label>
          <input
            type="text"
            name="specialInterests"
            value={preferences.specialInterests}
            onChange={handleChange}
            placeholder="e.g., photography, bird watching, surfing"
            className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
        
        {/* Buttons */}
        <div className="flex flex-col sm:flex-row justify-between gap-4 pt-6 border-t">
          <button
            type="button"
            onClick={handleTopRecommendations}
            disabled={loading}
            className="px-6 py-3 bg-gray-200 text-gray-800 font-medium rounded-lg hover:bg-gray-300 transition disabled:opacity-50 flex-1"
          >
            {loading ? 'Loading...' : 'Show Top Recommendations'}
          </button>
          
          <button
            type="submit"
            disabled={loading}
            className="px-6 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition disabled:opacity-50 flex-1"
          >
            {loading ? 'Creating Your Itinerary...' : 'Create Personalized Itinerary'}
          </button>
        </div>
      </form>
      
      {/* Loading Indicator */}
      {loading && (
        <div className="mt-8 text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          <p className="mt-2 text-gray-600">Generating your perfect itinerary...</p>
        </div>
      )}
      
      {/* Results Display */}
      {itinerary && (
        <div className="mt-8 pt-6 border-t">
          <h3 className="text-xl font-bold mb-4 text-green-700">
            {itinerary.destination ? `${itinerary.destination} Itinerary` : "Your Recommendations"}
          </h3>
          
          {itinerary.summary && (
            <p className="mb-4 text-gray-700 bg-blue-50 p-4 rounded-lg">
              {itinerary.summary}
            </p>
          )}
          
          {itinerary.itinerary && (
            <div className="space-y-6">
              {itinerary.itinerary.map((item, index) => (
                <div key={index} className="p-4 border-l-4 border-blue-500 bg-blue-50 rounded-r-lg">
                  <h4 className="font-bold text-lg">{item.time}</h4>
                  <div className="mt-2">
                    <p className="font-semibold">{item.activity} - {item.location}</p>
                    <p className="text-gray-700 mt-1">{item.description}</p>
                    <p className="text-sm text-gray-500 mt-1">Duration: {item.duration}</p>
                  </div>
                </div>
              ))}
            </div>
          )}
          
          {itinerary.hotel_recommendation && (
            <div className="mt-6 p-4 border-l-4 border-green-500 bg-green-50 rounded-r-lg">
              <h4 className="font-bold text-lg">Recommended Accommodation</h4>
              <div className="mt-2">
                <p className="font-semibold">{itinerary.hotel_recommendation.name}</p>
                <p className="text-gray-700">{itinerary.hotel_recommendation.location}</p>
                <p className="mt-1">{itinerary.hotel_recommendation.description}</p>
                <p className="text-sm text-gray-500 mt-1">
                  Price range: {itinerary.hotel_recommendation.price_range}
                </p>
              </div>
            </div>
          )}
          
          {itinerary.recommendations && (
            <div className="mt-6">
              <h4 className="font-bold text-lg mb-3">Top Recommendations</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {itinerary.recommendations.map((rec, index) => (
                  <div key={index} className="p-4 border border-gray-200 rounded-lg bg-gray-50">
                    <h5 className="font-bold">{rec.name}</h5>
                    <p className="text-gray-600 text-sm mt-1">{rec.type} • {rec.location}</p>
                    <p className="mt-2">{rec.description}</p>
                    <p className="text-sm text-blue-600 mt-1">
                      Best time to visit: {rec.best_time}
                    </p>
                  </div>
                ))}
              </div>
            </div>
          )}
          
          {itinerary.additional_tips && (
            <div className="mt-6 p-4 bg-yellow-50 rounded-lg">
              <h4 className="font-bold text-lg">Travel Tips</h4>
              <p className="mt-2">{itinerary.additional_tips}</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default ItineraryForm;
